worlditem.ai:
this.speedX = 0;
this.speedY = 0;
if (this.curState == "idle"){
	if (isNaN(this.bulletOriginX) || isNaN(this.bulletOriginY)){
		this.calculateFrameData();
	}

	// TODO: Technically, we COULD achieve through a call to "Shoot()" in an executeonfinish with the frames, right?
	this.Shoot("shoot_left", this.bulletOriginX, this.bulletOriginY, 20, 20);
}
else if (this.curState == "running"){
	var actor = this;
	this.moveToCoordinate(gsGame.playerObj.x, gsGame.playerObj.y, this.movementSpeed, 5, function(){
		actor.changeState("dying");
		gsGame.playerObj.changeState("shoot_left");
		gsGame.TriggerEvent("speechortext", 2, "<actor>rabbit</actor><bubbletype>!</bubbletype><text>You drive. I'll shoot!</text><color>white</color><font>20px Comic Sans MS</font>");
		gsGame.TriggerEvent("change_variable", 2, "<variable>backgroundSpeedX0</variable><increment>-1</increment><targetvalue>-30</targetvalue>");
		gsGame.TriggerEvent("change_variable", 2, "<variable>backgroundSpeedXNeg1</variable><increment>-0.5</increment><targetvalue>-2.5</targetvalue>");
	});
}

this.speedX = 0;
this.speedY = 0;
if (this.curState == "idle"){
	if (isNaN(this.bulletOriginX) || isNaN(this.bulletOriginY)){
		this.calculateFrameData();
	}

	// TODO: Technically, we COULD achieve through a call to "Shoot()" in an executeonfinish with the frames, right?
	this.Shoot("shoot_left", this.bulletOriginX, this.bulletOriginY, 20, 20);
}
else if (this.curState == "running"){
	var actor = this;
	this.moveToCoordinate(gsGame.playerObj.x, gsGame.playerObj.y, this.movementSpeed, 5, function(){
		actor.changeState("dying");
		gsGame.playerObj.changeState("shoot_left");
		gsGame.TriggerEvent("speechortext", 2, "<actor>rabbit</actor><bubbletype>!</bubbletype><text>You drive. I'll shoot!</text><color>white</color><font>20px Comic Sans MS</font>");
		gsGame.TriggerEvent("change_variable", 2, "<variable>backgroundSpeedX0</variable><increment>-1</increment><targetvalue>-30</targetvalue>");
		gsGame.TriggerEvent("change_variable", 2, "<variable>backgroundSpeedXNeg1</variable><increment>-0.5</increment><targetvalue>-2.5</targetvalue>");
	});
}

worlditem.collided:
var nameOfItemCollidedWith = gsGame.worldObjects[idOfItemCollidedWith].name;
if (nameOfItemCollidedWith != "enemyMissile" && 
	nameOfItemCollidedWith != "enemyBomber" && 
	nameOfItemCollidedWith != "enemyGorilla" && 
	nameOfItemCollidedWith.indexOf("enemyVehicle") == -1){
	// Decrement it's health to "kill" it
	this.health--;

	// Play the explposion sound
	gameEngine.soundArray["Explosion3"].play();

	// Kill the speed so the "explosion cloud" doesn't keep moving
	this.speedX = 0;
	this.speedY = 0;
}

worlditem.ai:
this.lifespan -= (gsGame.speedfactor != 1) ? (gameEngine.timeslice * gsGame.speedfactor) : gameEngine.timeslice;

if (this.lifespan <= 0){
	Log(gsGame.timestamp + " - " + "reticle lifespan expired");
	this.changeState("dying");
}

worlditem.collided:
// Only "take damage" against the enemyMissile
if (gsGame.worldObjects[idOfItemCollidedWith].name == "enemyMissile")
	this.health--;

worlditem.ai:
if (this.curState == "flying"){
	if (!this.reachedPeak){
		if (this.y > this.minY){
			this.speedX = this.speedX < 0 ? -5 : 5;
			this.speedY = parseFloat(-this.movementSpeed/2);
		}
		else{
			this.reachedPeak = true;
		}
	}
	else{
		if (this.y < this.maxY){
			this.speedX = this.speedX < 0 ? -10 : 10;
			this.speedY = parseFloat(this.movementSpeed/2);
		}
		else{
			this.speedY = 0;
			this.speedX = gsGame.backgroundSpeedX0;
			this.changeState("dying");
		}
	}
}

worlditem.collided:
var nameOfItemCollidedWith = gsGame.worldObjects[idOfItemCollidedWith].name;
// Only take damage from other barrels if they're dying and anything that's not the enemyReticle and don't take damage from the enemyGorilla
if ((nameOfItemCollidedWith != "enemyReticle") && (nameOfItemCollidedWith != "enemyGorilla") && (nameOfItemCollidedWith.indexOf("Vehicle") == -1)){
	if ((nameOfItemCollidedWith != "barrel" || nameOfItemCollidedWith == "barrel" && gsGame.worldObjects[idOfItemCollidedWith].curState == "dying"))
		this.health--;
}

frame.executeonfinish:
if (gsGame.playerLives <= 0){
	gsGame.GameOver();
}
else{
	// Decrement the playerLives by one
	gsGame.playerLives--;
	Log("Player Lives Left: " + gsGame.playerLives);
	// Reset the active state
	gsGame.playerObj.active = true;
	// Reset the health
	gsGame.playerObj.health = gsGame.playerObj.maxHealth;
	// Start the respawn countdown timer
	gsGame.playerRespawnTimer = gameEngine.GetCurrentTime();
	// Set the respawn variable
	gsGame.playerIsRespawning = true;
	// Change the state to something like idle
	this.changeState("idle");
}

worlditem.collided:
if (!gsGame.playerIsRespawning){
	var nameOfItemCollidedWith = gsGame.worldObjects[idOfItemCollidedWith].name;
	switch(nameOfItemCollidedWith){
		case "enemyMelee":
			var punchDistance = 30;
			if (gsGame.worldObjects[idOfItemCollidedWith].curState.indexOf("attack_down") > -1){
				// Make sure you don't punch the player beyond the limits
				if (gsGame.playerObj.y + punchDistance > gsGame.maxY){
					punchDistance = gsGame.maxY - gsGame.playerObj.y;
				}
				// Move the player the proper distance
				this.speedY = punchDistance;

				// Changed the state to Stunned
				this.changeState("stunned");
				// Set the gotHit flag
				this.gotHit = true;
				// Play the "PlayerGotHit" sound
				gameEngine.soundArray["PlayerGotHit"].play();
				// Take damage
				if (!gsGame.playerInvincible && !gameEngine.debugMode)
					this.health -= gsGame.worldObjects[idOfItemCollidedWith].baseDamage;
			}
			else if (gsGame.worldObjects[idOfItemCollidedWith].curState.indexOf("attack_up") > -1){
				// Make sure you don't punch the player beyond the limits
				if (gsGame.playerObj.y - punchDistance < gsGame.minY){
					punchDistance = gsGame.playerObj.y - gsGame.minY;
				}
				// Move the player the proper distance
				this.speedY = -punchDistance;

				// Changed the state to Stunned
				this.changeState("stunned");
				// Set the gotHit flag
				this.gotHit = true;
				// Play the "PlayerGotHit" sound
				gameEngine.soundArray["PlayerGotHit"].play();
				// Take damage
				if (!gsGame.playerInvincible && !gameEngine.debugMode)
					this.health -= gsGame.worldObjects[idOfItemCollidedWith].baseDamage;
			}
			else{
				break;
			}

			break;
		case "enemyGorilla":
		case "enemyVehiclePink":
		case "enemyVehicleYellow":
		case "enemyVehicleBlack":
		case "enemyVehicleBlue":
			// If the vehicles are in vehicle mode (meaning curState.indexOf("robot") == -1) AND they're not dying OR they're doing a fist smash then a touch by them will hurt
			if ((gsGame.worldObjects[idOfItemCollidedWith].curState.indexOf("robot") == -1 && gsGame.worldObjects[idOfItemCollidedWith].curState != "dying")|| gsGame.worldObjects[idOfItemCollidedWith].curState.indexOf("smash") != -1){
				if (!gsGame.playerInvincible && !gameEngine.debugMode)
					this.health -= gsGame.worldObjects[idOfItemCollidedWith].baseDamage;

				// Changed the state to Stunned
				this.changeState("stunned");
				// Set the gotHit flag
				this.gotHit = true;
				// Play the "PlayerGotHit" sound
				gameEngine.soundArray["PlayerGotHit"].play();
			}
			break;
		case "barrel":
		case "enemyMissile":
		case "enemyBomberBullet":
		case "barricadeConcrete":
		case "barricadeLaser":
			// Decrement health
			if (!gsGame.playerInvincible && !gameEngine.debugMode)
				this.health -= gsGame.worldObjects[idOfItemCollidedWith].baseDamage;

			// Changed the state to Stunned
			this.changeState("stunned");
			// Set the gotHit flag
			this.gotHit = true;
			// Play the "PlayerGotHit" sound
			gameEngine.soundArray["PlayerGotHit"].play();
			break;
	}
}

worlditem.ai:
this.speedX = 0;
this.speedY = 0;

if (this.curState.indexOf("idle") != -1) {
	if (!gsGame.isCutscene){
		this.moveToCoordinate(gsGame.playerObj.x, gsGame.playerObj.y, this.movementSpeed, 10, this.changeState.bind(this, "attack_" + (this.y > gsGame.playerObj.y ? "up" : "down")));
	}
}
else if (this.curState.indexOf("dying") > -1 || this.curState.indexOf("empty") > -1){
	this.speedX = gsGame.backgroundSpeedX0;
}

worlditem.collided:
var nameOfItemCollidedWith = gsGame.worldObjects[idOfItemCollidedWith].name;
switch(nameOfItemCollidedWith){
	case "enemyBomberBullet":
	case "playerBullet":
		// Decrement health
		this.health -= gsGame.worldObjects[idOfItemCollidedWith].baseDamage
		this.gotHit = true;
		break;
	case "enemyGorilla":
	case "enemyMelee":
		// Make sure you keep at least a minimum distance from each other so you all don't overlap on both the x AND y axis

		if (Math.abs(this.x - gsGame.worldObjects[idOfItemCollidedWith].x) <= 10){
			// If we're heading RIGHT
			if (this.speedX > 0){
				// If this one is the one that's BEHIND the other then this one needs to slow their roll
				if (this.x < gsGame.worldObjects[idOfItemCollidedWith].x)
					this.x = this.oldX;
			}
			// Else if we're heading LEFT
			if (this.speedX < 0){
				// If this one is the one that's IN FRONT OF the other then this one needs to slow their roll
				if (this.x > gsGame.worldObjects[idOfItemCollidedWith].x)
					this.x = this.oldX;
			}
		}

		if (Math.abs(this.y - gsGame.worldObjects[idOfItemCollidedWith].y) <= 10){
			// If we're heading DOWN
			if (this.speedY > 0){
				// If this one is the one that's ABOVE the other then this one needs to slow their roll
				if (this.y < gsGame.worldObjects[idOfItemCollidedWith].y)
					this.y = this.oldY;
			}
			// Else if we're heading UP
			if (this.speedY < 0){
				// If this one is the one that's BELOW the other then this one needs to slow their roll
				if (this.y > gsGame.worldObjects[idOfItemCollidedWith].y)
					this.y = this.oldY;
			}
		}
		break;
	case "barrel":
	case "barricadeLaser":
	case "barricadeConcrete":
		this.health = 0;
		this.gotHit = true;
		break;
}

worlditem.ai:
this.speedX = 0;
this.speedY = 0;

switch(this.curState){
	case "dying":
		this.speedY = this.movementSpeed / 4;
		this.speedX -= this.movementSpeed / 4;

		var min = this.x - (this.anchorpointX - this.animationX);
		var max = min + this.animationWidth;

		xpos = Math.floor(Math.random()*(max-min+1)+min);
		min = this.y - (this.anchorpointY - this.animationY);
		max = min + this.animationHeight;

		ypos = Math.floor(Math.random()*(max-min+1)+min);

		gsGame.TriggerEvent("spawn_item", 0, "<type>explosion</type><posx>" + xpos + "</posx><posy>" + ypos+ "</posy><speedx>0</speedx><speedy>0</speedy><state>dying</state>");
		break;

	case "prep_strafe_left":
		this.moveToCoordinate(gameEngine.canvas.width - 100, 100, this.movementSpeed, 0, this.changeState.bind(this, "strafe_shoot_left"));
		break;

	case "strafe_shoot_left":
		this.moveToCoordinate(100, 100, this.movementSpeed, 0, this.changeState.bind(this, "idle"));
		this.Shoot("shoot_down", this.bulletOriginX + 498, this.bulletOriginY);
		break;

	case "prep_strafe_right":
		this.moveToCoordinate(100, 100, this.movementSpeed, 0, this.changeState.bind(this, "strafe_shoot_right"));
		break;

	case "strafe_shoot_right":
		this.moveToCoordinate(gameEngine.canvas.width - 100, 100, this.movementSpeed, 0, this.changeState.bind(this, "idle"));
		this.Shoot("shoot_down", this.bulletOriginX, this.bulletOriginY);
		break;

	case "prep_launch_missiles":
		this.moveToCoordinate(parseInt(gameEngine.canvas.width /2), 100, this.movementSpeed, 0, this.changeState.bind(this, "launch_missiles"));
		break;

	case "launch_missiles":
		break;

	case "idle":
		var bomberObj = this;
		this.moveToCoordinate(!bomberObj.movingRight ? 200 : 1000, 200, this.movementSpeed, 20, function(){ bomberObj.movingRight = !bomberObj.movingRight; });
		break;

	case "enter":
		this.moveToCoordinate(parseInt(gameEngine.canvas.width / 2), 200, this.movementSpeed, 0, this.changeState.bind(this, "idle"));
		break;

	case "leaving":
		var bomberObj = this;
		this.moveToCoordinate(-150, -150, this.movementSpeed, 0, function(){ bomberObj.active = false; });
		break;
}

worlditem.collided:
if (gsGame.worldObjects[idOfItemCollidedWith].name == "playerBullet"){
	// Play the PlayerBulletHit.wav
	gameEngine.soundArray["PlayerBulletHit"].play();
	this.health--;
	this.gotHit = true;
	gsGame.lastHitEnemy = this.name;
}

var nameOfItemCollidedWith = gsGame.worldObjects[idOfItemCollidedWith].name;
if (nameOfItemCollidedWith == "player" || nameOfItemCollidedWith == "enemyMelee" || nameOfItemCollidedWith == "enemyGorilla"){
	this.health = 0;
	this.gotHit = true;
}
else if (nameOfItemCollidedWith.indexOf("Bullet") != -1){
	this.health --;
	this.gotHit = true;
}

var nameOfItemCollidedWith = gsGame.worldObjects[idOfItemCollidedWith].name;
if (nameOfItemCollidedWith == "player" || nameOfItemCollidedWith == "enemyMelee" || nameOfItemCollidedWith == "enemyGorilla"){
	this.health = 0;
	this.gotHit = true;
}
else if (nameOfItemCollidedWith.indexOf("Bullet") != -1){
	this.health --;
	this.gotHit = true;
}

frame.executeonfinish:
// This should happen after the middle frame
gsGame.TriggerEvent("spawn_item", 0, "<type>barrel</type><posx>" + (this.x) + "</posx><posy>" + (this.y - 100) + "</posy><speedx>" + (gsGame.playerObj.x < this.x-20 ? -5 : 5 ) + "</speedx><speedy>0</speedy><minY>" + (this.y - 200) + "</minY><maxY>" + (this.y + 30) + "</maxY><state>flying</state>");
// This should happen on the last frame
this.changeState("robot_idle");

this.changeState("robot_dead");

// Check to see if all the enemyVehicles are "robot_dead". If they are then switch them all to "dying".
if (gsGame.enemyVehiclePinkObj.curState == "robot_dead" && gsGame.enemyVehicleYellowObj.curState == "robot_dead" && gsGame.enemyVehicleBlackObj.curState == "robot_dead" && gsGame.enemyVehicleBlueObj.curState == "robot_dead" && gsGame.enemyGorillaObj.curState == "robot_dead"){
	 gsGame.enemyVehiclePinkObj.changeState("dying");
	 gsGame.enemyVehicleYellowObj.changeState("dying");
	 gsGame.enemyVehicleBlackObj.changeState("dying");
	 gsGame.enemyVehicleBlueObj.changeState("dying");
	 gsGame.enemyGorillaObj.changeState("dying");
}

worlditem.ai:
if (this.curState.indexOf("robot") != -1 && this.curState != "dying"){
	this.x = gsGame.enemyVehicleYellowObj.x - (gsGame.enemyVehicleYellowObj.x - gsGame.enemyVehicleYellowObj.hipposX);
	this.y = gsGame.enemyVehicleYellowObj.y - (gsGame.enemyVehicleYellowObj.y - gsGame.enemyVehicleYellowObj.hipposY);
}
else{
	this.speedX = 0;
	this.speedY = 0;
}

switch(this.curState){
	case "idle":
		// If there's an active "barrel" on the screen then we don't want to move
		var activeBarrel = false;
		for (var b = 0; b < gsGame.worldObjects.length ; b++){
			if (gsGame.worldObjects[b].name == "barrel" && gsGame.worldObjects[b].active){
				activeBarrel = true;
				break;
			}
		}

		if (!activeBarrel)
			this.moveToCoordinate(250, gsGame.playerObj.y, this.movementSpeed, 10, this.changeState.bind(this, "attack"));
		break;
	case "car_chasing":
		var vehicle = this;
		this.moveToCoordinate(!vehicle.movingRight ? 160 : 200, this.y, !vehicle.movingRight ? 2 : 5, 0, function(){ vehicle.movingRight = !vehicle.movingRight; });
		break;
	case "prep_car_passing":
		// Just sit there for a second and then changeState into car_passing. So put in an executeonfinish in the final frame of this state to changeState("car_passing")
		break;
	case "car_passing":
		var vehicle = this;
		// Speed off screen
		this.moveToCoordinate(1600, this.y, 30, 0, function(){ 
			// Deactivate this object
			vehicle.active = false;
			// See if there are any other vehicles alive. If not, then let's play the transforming sound
			if (!gsGame.enemyVehiclePinkObj.active && !gsGame.enemyVehicleYellowObj.active && !gsGame.enemyVehicleBlackObj.active && !gsGame.enemyVehicleBlueObj.active && !gsGame.enemyGorillaObj.active){
				gameEngine.soundArray["Transforming"].play();
			}
		});
		break;
	case "attack":
		// Actually nothing, the animation will take care of it all.
		break;
	case "robot_idle":
		// Just follow along the enemyVehicleYellow hipppos
		break;
	case "robot_leaving":
		// Just follow along the enemyVehicleYellow hipppos
		break;
	case "robot_attack":
		// Just follow along the enemyVehicleYellow hipppos and then spawn a barrel at the end of the animation.
		// We could potentially have a switch statement fall-through with "robot_idle" and the animation will handle the spawning
		break;
	case "robot_dying":
		// Just play a bunch of explosions for the duration of the dying frame within the bounds of the hitbox
		// When finished on that frame then do a changeState("robot_dead")

		var min = this.x - (this.anchorpointX - this.animationX);
		var max = min + this.animationWidth;
		xpos = Math.floor(Math.random()*(max-min+1)+min);					

		min = this.y - (this.anchorpointY - this.animationY);
		max = min + this.animationHeight;
		ypos = Math.floor(Math.random()*(max-min+1)+min);

		gsGame.TriggerEvent("spawn_item", 0, "<type>explosion</type><posx>" + xpos + "</posx><posy>" + ypos+ "</posy><speedx>0</speedx><speedy>0</speedy><state>dying</state>");
		break;
	case "robot_dead":
		// Don't do anything. Just sit there lookin busted.
		break;
	case "dying":
		// Fall off of the body. Ideally the pieces won't all fall at once so we'll have to stagger it through the duration of the dying frames?
		this.moveToCoordinate(this.x - 10, 800, 20, 0, null);
		break;
	case "retreating":
		// Just move off screen, deactivate yourself and overwrite the maxHealth with your currentHealth
		var vehicle = this;
		this.moveToCoordinate(-200, this.y, 20, 0, function(){
			vehicle.active = false;
			vehicle.maxHealth = vehicle.health;
		});
		break;
}

worlditem.collided:
if (gsGame.worldObjects[idOfItemCollidedWith].name == "playerBullet"){
	// Take damage if you're: a robot OR a car with more than 50% maxHealth
	if (this.curState.indexOf("robot") != -1 || (this.curState.indexOf("robot") == -1 && this.health >= this.maxHealth/2)){
		// Play the PlayerBulletHit.wav
		gameEngine.soundArray["PlayerBulletHit"].play();
		this.health--;
		this.gotHit = true;
		gsGame.lastHitEnemy = this.name;

		// If the enemyGorilla is in vehicle mode (meaning curState.indexOf("robot") is NOT -1 and our health is at 30% (we'll do the math) then changeState to "retreating"
		if (this.curState.indexOf("robot") == -1 && this.health < this.maxHealth/2){
			this.changeState("retreating");
		}					
	}
}

frame.executeonfinish:
this.changeState("robot_dead");

// Check to see if all the enemyVehicles are "robot_dead". If they are then switch them all to "dying".
if (gsGame.enemyVehiclePinkObj.curState == "robot_dead" && gsGame.enemyVehicleYellowObj.curState == "robot_dead" && gsGame.enemyVehicleBlackObj.curState == "robot_dead" && gsGame.enemyVehicleBlueObj.curState == "robot_dead" && gsGame.enemyGorillaObj.curState == "robot_dead"){
	 gsGame.enemyVehiclePinkObj.changeState("dying");
	 gsGame.enemyVehicleYellowObj.changeState("dying");
	 gsGame.enemyVehicleBlackObj.changeState("dying");
	 gsGame.enemyVehicleBlueObj.changeState("dying");
	 gsGame.enemyGorillaObj.changeState("dying");
}

worlditem.ai:
if (this.curState.indexOf("robot") != -1 && this.curState != "dying"){
	this.x = gsGame.enemyVehicleYellowObj.x - (gsGame.enemyVehicleYellowObj.x - gsGame.enemyVehicleYellowObj.headposX);
	this.y = gsGame.enemyVehicleYellowObj.y - (gsGame.enemyVehicleYellowObj.y - gsGame.enemyVehicleYellowObj.headposY);
}
else{
	this.speedX = 0;
	this.speedY = 0;
}

switch(this.curState){
	case "idle":
		var vehicle = this;
		this.moveToCoordinate(!vehicle.movingRight ? 140 : 200, this.y, !vehicle.movingRight ? 5 : 10, 0, function(){ vehicle.movingRight = !vehicle.movingRight; });
		break;
	case "prep_car_passing":
		// Just sit there for a second and then changeState into car_passing. So put in an executeonfinish in the final frame of this state to changeState("car_passing")
		break;
	case "car_passing":
		var vehicle = this;
		// Speed off screen
		this.moveToCoordinate(1500, this.y, 30, 0, function(){ 
			// Deactivate this object
			vehicle.active = false;
			// See if there are any other vehicles alive. If not, then let's play the transforming sound
			if (!gsGame.enemyVehiclePinkObj.active && !gsGame.enemyVehicleYellowObj.active && !gsGame.enemyVehicleBlackObj.active && !gsGame.enemyVehicleBlueObj.active && !gsGame.enemyGorillaObj.active){
				gameEngine.soundArray["Transforming"].play();
			}
		});
		break;
	case "robot_idle":
	case "robot_leaving":
	case "robot_look_right":
	case "robot_look_left":
		if (gsGame.playerObj.x - this.x > 60){
			this.changeState("robot_look_right");
		}
		else if (gsGame.playerObj.x - this.x < -60){
			this.changeState("robot_look_left");
		}
		else{
			this.changeState("robot_idle");
		}
		break;
	case "robot_shoot_down":
	case "robot_shoot_right":
	case "robot_shoot_left":
		var direction = "shoot_down";

		// Determine where it's gonna look while shooting
		if (gsGame.playerObj.x - this.x > 60){
			this.changeState("robot_shoot_right");
			direction = "shoot_downright";
		}
		else if (gsGame.playerObj.x - this.x < -60){
			this.changeState("robot_shoot_left");
			direction = "shoot_downleft";
		}
		else{
			this.changeState("robot_shoot_down");
		}

		var lastTime = this.lastTimeBulletFired;
		this.Shoot(direction, this.bulletOriginX, this.bulletOriginY);
		this.lastTimeBulletFired = lastTime;
		this.Shoot(direction, this.bulletOriginX + 101, this.bulletOriginY);

		this.changeState("robot_idle");

		break;
	case "robot_dying":
		// Just play a bunch of explosions for the duration of the dying frame within the bounds of the hitbox
		// When finished on that frame then do a changeState("robot_dead")

		var min = this.x - (this.anchorpointX - this.animationX);
		var max = min + this.animationWidth;
		xpos = Math.floor(Math.random()*(max-min+1)+min);					

		min = this.y - (this.anchorpointY - this.animationY);
		max = min + this.animationHeight;
		ypos = Math.floor(Math.random()*(max-min+1)+min);

		gsGame.TriggerEvent("spawn_item", 0, "<type>explosion</type><posx>" + xpos + "</posx><posy>" + ypos+ "</posy><speedx>0</speedx><speedy>0</speedy><state>dying</state>");
		break;
	case "robot_dead":
		// Don't do anything. Just sit there lookin busted.
		break;
	case "dying":
		// Fall off of the body. Ideally the pieces won't all fall at once so we'll have to stagger it through the duration of the dying frames?
		this.moveToCoordinate(this.x - 10, 800, 10, 0, null);
		break;
}

worlditem.collided:
if (gsGame.worldObjects[idOfItemCollidedWith].name == "playerBullet"){			
	// Take damage if you're: a robot OR a car with more than 50% maxHealth
	if (this.curState.indexOf("robot") != -1 || (this.curState.indexOf("robot") == -1 && this.health >= this.maxHealth/2)){
		gameEngine.soundArray["PlayerBulletHit"].play();
		this.health--;
		this.gotHit = true;
		gsGame.lastHitEnemy = this.name;
	}
}

frame.executeonfinish:
this.changeState("robot_dead");

// Check to see if all the enemyVehicles are "robot_dead". If they are then switch them all to "dying".
if (gsGame.enemyVehiclePinkObj.curState == "robot_dead" && gsGame.enemyVehicleYellowObj.curState == "robot_dead" && gsGame.enemyVehicleBlackObj.curState == "robot_dead" && gsGame.enemyVehicleBlueObj.curState == "robot_dead" && gsGame.enemyGorillaObj.curState == "robot_dead"){
	 gsGame.enemyVehiclePinkObj.changeState("dying");
	 gsGame.enemyVehicleYellowObj.changeState("dying");
	 gsGame.enemyVehicleBlackObj.changeState("dying");
	 gsGame.enemyVehicleBlueObj.changeState("dying");
	 gsGame.enemyGorillaObj.changeState("dying");
}

worlditem.ai:
if (this.curState.indexOf("robot") != -1 && this.curState != "dying"){
	this.x = gsGame.enemyVehicleYellowObj.x - (gsGame.enemyVehicleYellowObj.x - gsGame.enemyVehicleYellowObj.leftarmposX);
	this.y = gsGame.enemyVehicleYellowObj.y - (gsGame.enemyVehicleYellowObj.y - gsGame.enemyVehicleYellowObj.leftarmposY);
}
else{
	this.speedX = 0;
	this.speedY = 0;
}

switch(this.curState){
	case "idle":
		var vehicle = this;
		this.moveToCoordinate(!vehicle.movingRight ? 120 : 200, this.y, !vehicle.movingRight ? 2 : 5, 0, function(){ vehicle.movingRight = !vehicle.movingRight; });
		break;
	case "prep_car_passing":
		// Nothing needs to specified here, the frames' "executeonfinish" will handle the changeState to "car_passing"
		break;
	case "car_passing":
		var vehicle = this;
		// Speed off screen
		this.moveToCoordinate(1500, this.y, 30, 0, function(){ 
			// Deactivate this object
			vehicle.active = false;
			// See if there are any other vehicles alive. If not, then let's play the transforming sound
			if (!gsGame.enemyVehiclePinkObj.active && !gsGame.enemyVehicleYellowObj.active && !gsGame.enemyVehicleBlackObj.active && !gsGame.enemyVehicleBlueObj.active && !gsGame.enemyGorillaObj.active){
				gameEngine.soundArray["Transforming"].play();
			}
		});
		break;
	case "robot_dying":
		// Just play a bunch of explosions for the duration of the dying frame within the bounds of the hitbox
		// When finished on that frame then do a changeState("robot_dead")

		var min = this.x - (this.anchorpointX - this.animationX);
		var max = min + this.animationWidth;
		xpos = Math.floor(Math.random()*(max-min+1)+min);					

		min = this.y - (this.anchorpointY - this.animationY);
		max = min + this.animationHeight;
		ypos = Math.floor(Math.random()*(max-min+1)+min);

		gsGame.TriggerEvent("spawn_item", 0, "<type>explosion</type><posx>" + xpos + "</posx><posy>" + ypos+ "</posy><speedx>0</speedx><speedy>0</speedy><state>dying</state>");
		break;
	case "dying":
		// Fall off of the body. Ideally the pieces won't all fall at once so we'll have to stagger it through the duration of the dying frames?
		this.moveToCoordinate(this.x - 30, 800, 20, 0, null);
		break;
}

