			}
		});
		break;
	case "robot_enter":
		this.moveToCoordinate(600, 300, 30, 0, this.changeState.bind(this, "robot_idle"));
		break;
	case "robot_dead":
	case "robot_idle":
	case "robot_leaving":
		// Bob up and down
		var vehicle = this;
		this.moveToCoordinate(this.curState == "robot_leaving" ? -500 : this.x, vehicle.movingUp ? 225 : 275, 2, 0, function(){ vehicle.movingUp = !vehicle.movingUp; });
		break;
	case "robot_launch_missiles":
		// Nothing needs to specified here, the frames' "executeonfinish" will handle the missile spawns
		break;
	case "robot_dying":
		// Just play a bunch of explosions for the duration of the dying frame within the bounds of the hitbox
